import { useContext, useState } from "react";
import Context from "./Context";

const SelectReset = () => {
  const timeContext = useContext(Context);

  const secondsArray = [10, 20, 30, 40, 50];

  const [seconds, setSeconds] = useState();

  const timeIndecis = (e) => {
    setSeconds(e.currentTarget.value);
  };

  if (seconds) {
    console.log(seconds);
    timeContext.time = seconds;
  }

  return (
    <div id="selectReset">
      <select onChange={timeIndecis}>
        {secondsArray.map((time, i) => (
          <option value={time} key={i}>
            {time}
            {" sec"}
          </option>
        ))}
      </select>
      <button>Reset</button>
    </div>
  );
};

export default SelectReset;
