import { useState } from "react";
import { CountdownCircleTimer } from "react-countdown-circle-timer";

import "./style.css";

const CounterTimer = () => {
  const secondsArray = [10, 20, 30, 40, 50];

  const [isSecondClick, setIsSecondClick] = useState();
  const [resetClicked, setResetClicked] = useState(false);
  const [seconds, setSeconds] = useState();

  let clickCount = 0;

  const onClickReset = () => {
    for (let i = 0; i < 1; i++) {
      clickCount++;
    }

    if (clickCount > 0) {
      if (resetClicked > 0) {
        clickCount = 0;
      }
      setResetClicked(clickCount);
      clickCount = 0;
    }

    if (isSecondClick) {
      setIsSecondClick(false);
      console.log(resetClicked);
      return;
    }
    setIsSecondClick(true);
  };

  console.log(resetClicked);

  const timeIndecis = (e) => {
    setSeconds(e.currentTarget.value);
  };

  const counterTimerBody = ({ remainingTime }) => {
    if (remainingTime === 0) {
      return (
        <div style={{ display: "flex", color: "red", fontSize: "20px" }}>
          Too late
        </div>
      );
    }

    return (
      <div className="timerBody">
        <div className="remainingTime">Remaining time</div>
        <div id="timer">{remainingTime}</div>
        <div className="remainingTime">seconds</div>
      </div>
    );
  };

  return (
    <div>
      <h1>
        <a
          href="https://www.reactlabs.ai/"
          target="_blank"
          rel="noopener noreferrer"
        >
          React Labs
        </a>
      </h1>
      <h2>ReactJS Assignment</h2>
      <div id="countDownTimer">
        <CountdownCircleTimer
          key={isSecondClick}
          size={250}
          isPlaying
          duration={seconds && resetClicked ? seconds : secondsArray[0]}
          colors={[
            ["#34AD00", 0.55],
            ["#B3BB2B", 0.44],
            ["#FF0000", 0.33],
          ]}
        >
          {counterTimerBody}
        </CountdownCircleTimer>
      </div>
      <div id="selectReset">
        <select onChange={timeIndecis}>
          {secondsArray.map((time, i) => (
            <option value={time} key={i}>
              {time}
              {" sec"}
            </option>
          ))}
        </select>
        <button onClick={onClickReset}>Reset</button>
      </div>
    </div>
  );
};

export default CounterTimer;
